if not feature_flags.space_travel then
    return
end

local aoe_damage = {
    type = "script",
    effect_id = ASTEROID_KILL,
    probability = 0.2
}

for _, asteroid in pairs(data.raw.asteroid) do
    local add_kill_trigger = false
    for _, name in pairs({'medium','big','huge'}) do
        if string.find(asteroid.name, name, 1, true) then
            add_kill_trigger = true
            break
        end
    end

    if add_kill_trigger then
        if type(asteroid.dying_trigger_effect) == "table" then
            table.insert(asteroid.dying_trigger_effect, aoe_damage)
        else
            asteroid.dying_trigger_effect = {}
            table.insert(asteroid.dying_trigger_effect, aoe_damage)
        end
    end
end 